import React, { useState } from "react";
// import { Form, Card, Button } from "react-bootstrap";
import {
  Modal,
  Form,
  Col,
  Row,
  Container,
  FormGroup,
  Button,
} from "react-bootstrap";
import validator from "validator";
import Card from "react-bootstrap/Card";
import { Link } from "react-router-dom";
// creating functional component ans getting props from app.js and destucturing them
const StepOne = ({ nextStep, handleFormData, value }) => {
  //creating error state for validation
  const [error, setError] = useState(false);

  // after form submit validating the form data using validator
  const submitFormData = (e) => {
    e.preventDefault();

    // checking if value of first name and last name is empty show error else take to step 2
    console.log("value", value);
    if (
      validator.isEmpty(value.firstName) ||
      validator.isEmpty(value.lastName) ||
      validator.isEmpty(value.gender) ||
      validator.isEmpty(value.cnic) ||
      validator.isEmpty(value.dob) ||
      validator.isEmpty(value.martialStatus) ||
      validator.isEmpty(value.religion)
    ) {
      setError(true);
      console.log("setError");
    } else {
      nextStep();
      console.log("nextstep");
    }
  };

  return (
    <>
      {/* <div className="col">
    <h3 className="page-title">Add Employees</h3>
  <ul className="breadcrumb" style={{ backgroundColor: "#f7f7f7" }}>
     <li className="breadcrumb-item">
       <Link to="/" style={{ color: "#1f1f1f" }}>
         Dashboard
       </Link>
     </li>
     <li className="breadcrumb-item active">Add Employee</li>
  </ul>
  </div> */}
      <div>
        <Card style={{ marginTop: 100 }}>
          <Card.Body>
            <Form onSubmit={submitFormData}>
              <h4>Personal Information</h4>
              <hr></hr>
              <Row>
                <Col xxl="6" xl="6" lg="6" md="6">
                  <Form.Group className="mb-3">
                    <Form.Label>First Name</Form.Label>
                    <Form.Control
                      required
                      // style={{ border: error ? "2px solid red" : "" }}
                      name="firstName"
                      defaultValue={value.firstName}
                      // value={emp.firstname}
                      type="text"
                      placeholder="First Name"
                      onChange={handleFormData("firstName")}
                    />
                  </Form.Group>
                </Col>
                <Col xxl="6" xl="6" lg="6" md="6">
                  <Form.Group className="mb-3">
                    <Form.Label>Last Name</Form.Label>
                    <Form.Control
                      required
                      // style={{ border: error ? "2px solid red" : "" }}
                      name="lastName"
                      defaultValue={value.lastName}
                      type="text"
                      placeholder="Last Name"
                      onChange={handleFormData("lastName")}
                    />
                  </Form.Group>
                </Col>
              </Row>
              <Row>
                <Col xxl="6" xl="6" lg="6" md="6">
                  <Form.Group
                    as={Col}
                    controlId="formGridgender"
                    className="formmargin"
                  >
                    <Form.Label>Gender</Form.Label>
                    <Form.Select
                      required
                      name="gender"
                      defaultValue={value.gender}
                      //   value={emp.gender}
                      // value={props.value2}
                      //   onChange={handleinput}
                      // onChange={props.onChange}
                      onChange={handleFormData("gender")}
                    >
                      <option value="" selected hidden disabled>
                        Please Select
                      </option>
                      <option>Male</option>
                      <option>Female</option>
                      <option>Other</option>
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col xl="6" lg="6" md="6">
                  <Form.Group
                    as={Col}
                    controlId="formGridFirstName"
                    className="formmargin"
                  >
                    <Form.Label>National ID </Form.Label>
                    <Form.Control
                      type="number"
                      required
                      name="cnic"
                      placeholder="CNIC"
                      //   value={emp.cnic}
                      defaultValue={value.cnic}
                      // value={props.value3}
                      //   onChange={handleinput}
                      // onChange={props.onChange}
                      //   disabled={disableFields}
                      onChange={handleFormData("cnic")}
                    />
                  </Form.Group>
                </Col>
              </Row>
              <Row>
                <Col xxl="6" xl="6" lg="6">
                  <Form.Group
                    as={Col}
                    controlId="formGridLastName"
                    className="formmargin"
                  >
                    <Form.Label>D-0-B</Form.Label>
                    {/* <DatePicker
                          
                            required
                            selected={emp.dob}
                            onChange={(date)=>{setEmp({...emp,dob:moment(date).format('dd/mm/yyyy')})}}
                            // dateFormat='d MMMM, yyyy'
                            dateFormat='dd/mm/yyyy'
                            dropDownMode="select"
                          /> */}
                    <Form.Control
                      type="date"
                      required
                      placeholder="dd/mm/yyyy"
                      name="dob"
                      //   value={emp.dob}
                      // value={props.value4}
                      defaultValue={value.dob}
                      //   onChange={handleinput}
                      // onChange={props.onChange}
                      //   disabled={disableFields}
                      onChange={handleFormData("dob")}
                    />
                  </Form.Group>
                </Col>

                <Col xl="6" lg="6">
                  <Form.Group
                    as={Col}
                    controlId="formGridMartialStatus"
                    className="formmargin"
                  >
                    <Form.Label>Martial Status</Form.Label>
                    <Form.Select
                      required
                      name="martialStatus"
                      placeholder="martial status.."
                      //   value={emp.martialStatus}
                      defaultValue={value.martialStatus}
                      // value={props.value5}
                      //   onChange={handleinput}
                      // onChange={props.onChange}
                      //   disabled={disableFields}
                      onChange={handleFormData("martialStatus")}
                    >
                      <option value="" selected hidden disabled>
                        Please Select
                      </option>
                      <option>Single</option>
                      <option>Married</option>
                      <option>Divorced</option>
                      <option>widow</option>
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col xl="6" lg="6">
                  <Form.Group
                    as={Col}
                    controlId="formGridLastName"
                    className="formmargin"
                  >
                    <Form.Label>Religion</Form.Label>
                    <Form.Select
                      required
                      name="religion"
                      placeholder="Religion"
                      //   value={emp.religion}
                      defaultValue={value.religion}
                      // value={props.value6}
                      //   onChange={handleinput}
                      // onChange={props.onChange}
                      //   disabled={disableFields}
                      onChange={handleFormData("religion")}
                    >
                      <option value="" selected hidden disabled>
                        Please Select
                      </option>
                      <option>Islam</option>
                      <option>Christianity</option>
                      <option>Buddhism</option>
                      <option> Sikhism</option>
                      <option> Hinduism</option>
                      <option>Bahá’í</option>
                      <option>Confucianism</option>
                      <option>Jainism</option>
                      <option>Judaism</option>
                      <option>Zoroastrianism</option>
                      <option>Druze</option>
                    </Form.Select>
                  </Form.Group>
                </Col>
              </Row>
              <Button variant="primary" type="submit">
                Next
              </Button>
