import React, { useState } from "react";
import {
  Typography,
  TextField,
  Button,
  Stepper,
  Step,
  StepLabel,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import {
  Modal,

  Form,
  Col,
  Row,
  Container,
  FormGroup,
} from "react-bootstrap";
const useStyles = makeStyles((theme) => ({
  button: {
    marginRight: theme.spacing(1),
  },
}));

function getSteps() {
  return [
    "Personal Information",
    "Address & Region",
    "Education Information",
    "Previous Information",
    "Employement Details",
    "Bank Details"
  ];
}

function getStepContent(step) {
  switch (step) {
    case 0:
      return (
        <>
          {/* <TextField
            id="first-name"
            label="First Name"
            variant="outlined"
            placeholder="Enter Your First Name"
            fullWidth
            margin="normal"
            name="firstName"
          />
          <TextField
            id="last-name"
            label="Last Name"
            variant="outlined"
            placeholder="Enter Your Last Name"
            fullWidth
            margin="normal"
            name="lastName"
          />
          <TextField
            id="nick-name"
            label="Nick Name"
            variant="outlined"
            placeholder="Enter Your Nick Name"
            fullWidth
            margin="normal"
            name="nickName"
          /> */}
          <div className="w-75" id="responsivewidth">
                  <Col>
                    <Row >
                      <Col xxl="6" xl="6" lg="6" md="6">
                        <Form.Group
                          as={Col}
                          controlId="formGridFirstName"
                          className="formmargin"
                        >
                          <Form.Label>First Name</Form.Label>
                          <Form.Control
                            type="text"
                            required
                            name="firstname"
