import React, { useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import "./Employee.css";
import pp from "./avatar.png";
import { useState } from "react";
import Card from "../Emp Card/EmpCard";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import "react-notifications/lib/notifications.css";
import {
  NotificationContainer,
  NotificationManager,
} from "react-notifications";
import {
  Modal,
  Button,
  Form,
  Col,
  Row,
  Container,
  FormGroup,
} from "react-bootstrap";
import Table from "react-bootstrap/Table";
import ViewListIcon from "@mui/icons-material/ViewList";
import ViewModuleIcon from "@mui/icons-material/ViewModule";
import ToggleButton from "@mui/material/ToggleButton";
import ToggleButtonGroup from "@mui/material/ToggleButtonGroup";
import TableView from "../TableView/Table";
import { useNavigate } from "react-router-dom";
import ReactFlags from "react-flags-select";
import { cities } from "./cities";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import EmployeeData from "./EmployeeData";
const moment = require("moment");

const AllEmployees = () => {
  let navigate = useNavigate();
  const url2 = "/departments";
  const url = "/employees";
  const url1 = "/auth/register";
  const [dep, setDep] = useState([]);
  const [datas, setData] = useState();
  // const [modalShow, setModalShow] = React.useState(false);
  const handleClose = () => setShow(false);
  const Closechildmodal = () => setShowChildModel(false);
  const Closechildmodal1 = () => setShowChildModel1(false);
  const showChildModel = () => setShowChildModel(true);
  const showChildModel1 = () => setShowChildModel1(true);
  const handleShow = () => setShow(true);
  const [childModel, setShowChildModel] = useState(false);
  const [childModel1, setShowChildModel1] = useState(false);
  const [show, setShow] = useState(false);
  const [birth,setbirth] = useState("")
  const [testUpdate, setTestUpdate] = useState(false);

  const [list, setlist] = useState(false);
  //for profile pic
  const [file, setfile] = useState();
  //Employee Add
  const [emp, setEmp] = useState({
    profilepic: "",
    firstname: "",
    lastname: "",
    dob: "",
    cnic: "",
    gender: "",
    martialStatus: "",
    religion: "",
    jobtitle: "",
    username: "",
    primaryemail: "",
    secondaryemail: "",
    password: "",
    primaryphone: "",
    secondaryphone: "",
    permanentaddress: "",
    temporaryaddress: "",
    province: "",
    city: "",
    postalCode: "",
    departments: "",
    designation: "",
    joiningdate: "",
    educationdetails: "",
    employementhistory: "",

    currentSalary: "",
    employementstatus: "",
    //bank information
    bankname: "",
    paymentmode: "",
    accounttitle: "",
    accountno: "",
    IBAN: "",
    branchcode: "",
    country:"",
    //degree info
  });
  //educaion details add and remove //
  const [education, seteducation] = useState([]);
  const [selected, setSelected] = useState("");
  const [country, setcountry] = useState("");
  const [employement, setemployement] = useState([]);
  const [empdetails, setempdetails] = useState({
    company: "",
    position: "",
    joiningdate: "",
    resignationdate: "",
    duration: "",
    jobdescription: "",
  });
  const [details, setdetails] = useState({
    degreetitle: "",
    institute: "",
    start: "",
    end: "",
    status: "",
  });
  const removeitem = (i) =>{
        const temp = education;
        temp.splice(i,1); 
        console.log("splice",temp)
        seteducation(temp);
        setTestUpdate(!testUpdate)
  }
  const removemployement = (i) =>{
    const temp = employement;
    employement.splice(i,1); 
    console.log("splice",temp)
    setemployement(temp);
    setTestUpdate(!testUpdate)
  }
  const addhistory = () => {
    var empl = employement;
    empl.push({
      company: empdetails.company,
      position: empdetails.position,
      joiningdate: empdetails.joiningdate,
      resignationdate: empdetails.resignationdate,
      duration: empdetails.duration,
      jobdescription: empdetails.jobdescription,
    });
    console.log("emplllllllll", empl);
    setemployement(empl);
    setEmp({ ...emp, employementhistory: empl });
  };

  let name, value;
  const handleinput = (e) => {
    console.log(e);
    name = e.target.name;
    value = e.target.value;
    setEmp({ ...emp, [name]: value });
  };

  const handleeducationdetails = async (e) => {
    let name, value;

    name = e.target.name;
    value = e.target.value;

    await setdetails({
      ...details,   
      [name]: value,
    });
  };

  //handle user input form data

  const handleempinput = async (e) => {
    let name, value;

    name = e.target.name;
    value = e.target.value;

    await setempdetails({
      ...empdetails,
      [name]: value,
    });
  };

  const addeducation = () => {
    var temp = education;
    temp.push({
      degreetitle: details.degreetitle,
      institute: details.institute,
      start: details.start,
      end: details.end,
      status: details.status,
    });
    seteducation(temp);
    setEmp({ ...emp, educationdetails: education });

  };

  const handleempinputJoiningDate = async (e) => {
    let name, value;

    name = e.target.name;
    value = e.target.value;

    console.log("change", empdetails);

    var a = moment(empdetails.resignationdate);
    var b = moment(e.target.value);

    var years = a.diff(b, "year");
    b.add(years, "years");

    var months = a.diff(b, "months");
    b.add(months, "months");

    var days = a.diff(b, "days");

    console.log(years + " years " + months + " months " + days + " days");

    await setempdetails({
      ...empdetails,
      [name]: e.target.value,
      duration: `${years}  years  ${months}  months  ${days} days`,
    });
  };

  const handleempinputResignationDate = async (e) => {
    let name, value;

    name = e.target.name;
    value = e.target.value;

    var a = moment(e.target.value);
    var b = moment(empdetails.joiningdate);

    var years = a.diff(b, "year");
    b.add(years, "years");

    var months = a.diff(b, "months");
    b.add(months, "months");

    var days = a.diff(b, "days");

    console.log(years + " years " + months + " months " + days + " days");

    await setempdetails({
      ...empdetails,
      [name]: e.target.value,
      duration: `${years}  years  ${months}  months  ${days} days`,
    });
  };
   ///education & employement details code end;

   //list and grid view start
  const [view, setView] = useState(false);
  const handleChange = (event, nextView) => {
    setView(!view);
    setView(nextView);
    setView(!view);
    // setlist(nextView)
    console.log(nextView);
  };
  //   const handleChange = () => {
  //     setlist(!list)
  //   };
  //submitting values of user
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (file) {
      const data =new FormData();
      const filename = Date.now() + file.name;
      data.append("name", filename);
      data.append("file", file);
      emp.profilepic = filename;
      try {
        await axios.post("/upload",data);
      } catch (err) {
        console.log(err);
        NotificationManager.error("Pic not Uploaded");
      }
    }

    try {
      const res = await axios.post(url1, emp);
      console.log(res);
      res && NotificationManager.success("Sucessfully Added Employee");
      // res && window.location.replace("/employees");
    } catch (error) {
      console.log(error);
      NotificationManager.error("Something went wrong ");
    }
  };
  //fetching employees data
  const fetchData = async () => {
    try {
      const res = await axios.get(url);
      console.log(res.data);
      const data = res.data.employees;
      setData(data);
    } catch (error) {
      console.log(error);
      NotificationManager.error("Api Error 404");
    }
  };
  //getting Employees
  const getEmp = async () => {
    try {
      const res = await axios.get(url2);
      const datas = res.data.departments;
      console.log("departments", datas);
      setDep(datas);
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    fetchData();
    getEmp();
  }, []);
  const PF = "http://localhost:5002/images/";
  return (
    <>
      <div className="content-wrapper" style={{ backgroundColor: "#f7f7f7" }}>
        {/* Content Header (Page header) */}
        <section className="content-header">
          <div className="container-fluid">
            <div className="row align-items-center">
              <div className="col">
                <h3 className="page-title">Employee</h3>
                <ul
                  className="breadcrumb"
                  style={{ backgroundColor: "#f7f7f7" }}
                >
                  <li className="breadcrumb-item">
                    <Link to="/" style={{ color: "#1f1f1f" }}>
                      Dashboard
                    </Link>
                  </li>
                  <li className="breadcrumb-item active">Employee</li>
                </ul>
              </div>
              <div className="col-auto float-end ms-auto">
                <a
                  className="btn add-btn "
                  data-bs-toggle="modal"
                  data-bs-target="#add_employee"
                  onClick={handleShow}
                >
                  <i
                    className="fa fa-plus"
                    style={{ fontSize: "14px", marginRight: "2px" }}
                  >
                    {" "}
                  </i>
                  Add Employee
                </a>
              </div>

              <div
                className="mt-4"
                style={{ display: "flex", justifyContent: "flex-end" }}
              >
                <div>
                  <ToggleButtonGroup
                    orientation="horizontal"
                    value={view}
                    exclusive
                    onChange={handleChange}
                  >
                    <ToggleButton
                      value="module"
                      aria-label="module"
                      selected={!view}
                    >
                      <ViewModuleIcon />
                    </ToggleButton>
                    <ToggleButton
                      value="list"
                      aria-label="list"
                      selected={view}
                    >
                      <ViewListIcon />
                    </ToggleButton>
                  </ToggleButtonGroup>
                </div>
              </div>
            </div>
          </div>
          {/* /.container-fluid */}
        </section>
        {/* Main content */}
        <section className="content" style={{ paddingBottom: "70px" }}>
          <div className="container-fluid">
            <div
              className="row  gy-3  vertical-scrollable"
              style={{ display: "flex", flexWrap: "wrap" }}
            >
              {/* list and grid view */}
              {view
                ? datas && <TableView data={datas} />
                : datas &&
                  datas.map((p, i) => {
                    return (
                      <div
                        className="col-md-4 col-sm-6 col-12 col-lg-4 col-xl-3"
                        key={i}
                      >
                        <div>
                          <Card data={p} />
                        </div>
                      </div>
                    );
                  })}
            </div>
          </div>
        </section>
        <NotificationContainer />
        <Container style={{justifyContent:'center',alignItems:'center',justifyItems:'center',width:'60%',height:'60%'}}>
          <Modal
            aria-labelledby="contained-modal-title-vcenter"
            centered
            show={show}
            onHide={handleClose}
            dialogClassName="my-modal"
            style={{width:'60%',height:'60%',justifyItems:'center',justifyContent:'center',alignItems:'center',justifyItems:'center'}}
          >
            <Modal.Header closeButton>
              <Modal.Title
                id="contained-modal-title-vcenter "
                style={{ textAlign: "center" }}
              >
                <h5> Add Employee</h5>
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Container fluid>
                <Form onSubmit={handleSubmit}>
                <Form.Group>
                {/* <Row>
                  <div className="py-3">
                    <h4>Personal Information</h4>
                    <hr
                      style={{
                        fontWeight: "bold",
                        borderWidth: "2px",
                        border: "1px solid black",
                      }}
                    ></hr>
                  </div>

                   <div className="responsiveform d-lg-flex d-lg-flex d-md-flex justify-content-between  ">
                   <div className="w-75" id="responsivewidth">
                  <Col>
                    <Row >
                      <Col xxl="6" xl="6" lg="6" md="6">
                        <Form.Group
                          as={Col}
                          controlId="formGridFirstName"
                          className="formmargin"
                        >
                          <Form.Label>First Name</Form.Label>
                          <Form.Control
                            type="text"
                            required
                            name="firstname"
                            placeholder="First Name"
                            value={emp.firstname}
                            onChange={handleinput}
                          />
                        </Form.Group>
                      </Col>
                      <Col xxl="6" xl="6" lg="6" md="6">
                        <Form.Group
                          as={Col}
                          controlId="formGridLastName"
                          className="formmargin"
                        >
                          <Form.Label>Last Name</Form.Label>
                          <Form.Control
                            type="text"
                            required
                            name="lastname"
                            placeholder="Last Name"
                            value={emp.lastname}
                            onChange={handleinput}
                          />
                        </Form.Group>
                      </Col>
                    </Row>
                    <Row>
                      <Col xxl='6' xl='6' lg='6' md='6'>
                        <Form.Group
                          as={Col}
                          controlId="formGridgender"
                          className="formmargin"
                        >
                          <Form.Label>Gender</Form.Label>
                          <Form.Select
                            required
                            name="gender"
                            value={emp.gender}
                            onChange={handleinput}
                          >
                            <option value="" selected hidden disabled>
                              Please Select
                            </option>
                            <option>Male</option>
                            <option>Female</option>
                            <option>Other</option>
                          </Form.Select>
                        </Form.Group>
                      </Col>
                      <Col xl='6' lg="6" md="6">
                        <Form.Group
                          as={Col}
                          controlId="formGridFirstName"
                          className="formmargin"
                        >
                          <Form.Label>National ID </Form.Label>
                          <Form.Control
                            type="number"
                            required
                            name="cnic"
