import React, { useState } from "react";
// import { Form, Card, Button } from "react-bootstrap";
import validator from "validator";
import {
  Modal,
  Form,
  Col,
  Row,
  Container,
  FormGroup,
  Button,
} from "react-bootstrap";
// import validator from "validator";
import Card from "react-bootstrap/Card";
import axios from "axios";
import Table from "react-bootstrap/Table";
import "react-notifications/lib/notifications.css";
import {
  NotificationContainer,
  NotificationManager,
} from "react-notifications";
const moment = require("moment");
// creating functional component ans getting props from app.js and destucturing them
const StepFour = ({ nextStep, handleFormData, prevStep, values }) => {
  //creating error state for validation
  const url1 = "/auth/register";
  const [error, setError] = useState(false);
  const [file, setfile] = useState();
  const [childModel, setShowChildModel] = useState(false);
  const showChildModel = () => setShowChildModel(true);
  const Closechildmodal = () => setShowChildModel(false);

  const [testUpdate, setTestUpdate] = useState(false);
  const [education, seteducation] = useState([]);
  const [empdetails, setempdetails] = useState({
    company: "",
    position: "",
    joiningdate: "",
    resignationdate: "",
    duration: "",
    jobdescription: "",
  });

  const addhistory = () => {
    var empl = employement;
    empl.push({
      company: empdetails.company,
      position: empdetails.position,
      joiningdate: empdetails.joiningdate,
      resignationdate: empdetails.resignationdate,
      duration: empdetails.duration,
      jobdescription: empdetails.jobdescription,
    });
    console.log("emplllllllll", empl);
    setemployement(empl);
    setEmp({ ...emp, employementhistory: empl });
  };

  const handleempinputJoiningDate = async (e) => {
    let name, value;

    name = e.target.name;
    value = e.target.value;

    console.log("change", empdetails);

    var a = moment(empdetails.resignationdate);
    var b = moment(e.target.value);

    var years = a.diff(b, "year");
    b.add(years, "years");

    var months = a.diff(b, "months");
    b.add(months, "months");

    var days = a.diff(b, "days");

    console.log(years + " years " + months + " months " + days + " days");

    await setempdetails({
      ...empdetails,
      [name]: e.target.value,
      duration: `${years}  years  ${months}  months  ${days} days`,
    });
  };

  const handleempinputResignationDate = async (e) => {
    let name, value;

    name = e.target.name;
    value = e.target.value;

    var a = moment(e.target.value);
    var b = moment(empdetails.joiningdate);

    var years = a.diff(b, "year");
    b.add(years, "years");

    var months = a.diff(b, "months");
    b.add(months, "months");

    var days = a.diff(b, "days");

    console.log(years + " years " + months + " months " + days + " days");

    await setempdetails({
      ...empdetails,
      [name]: e.target.value,
      duration: `${years}  years  ${months}  months  ${days} days`,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (file) {
      const data = new FormData();
      const filename = Date.now() + file.name;
      data.append("name", filename);
      data.append("file", file);
      emp.profilepic = filename;
      try {
        await axios.post("/upload", data);
      } catch (err) {
        console.log(err);
        NotificationManager.error("Pic not Uploaded");
      }
    }

    try {
      const res = await axios.post(url1, emp);
      console.log(res);
      res && NotificationManager.success("Sucessfully Added Employee");
      // res && window.location.replace("/employees");
    } catch (error) {
      console.log(error);
      NotificationManager.error("Something went wrong ");
    }
  };

  //    const handleinput = (e) => {
  //         console.log(e);
  //         name = e.target.name;
  //         value = e.target.value;
  //         setEmp({ ...emp, [name]: value });
  //       };

  // after form submit validating the form data using validator
  const submitFormData = (e) => {
    e.preventDefault();

    // checking if value of first name and last name is empty show error else take to next step
    if (
      validator.isEmpty(values.company) ||
      validator.isEmpty(values.position) ||
      validator.isEmpty(values.joiningdate) ||
      validator.isEmpty(values.resignationdate) ||
      validator.isEmpty(values.jobdescription)
    ) {
      setError(true);
      console.log("setError1");
    } else {
      nextStep();
      console.log("nextstep1");
    }
  };
  const removeitem = (i) => {
    const temp = education;
    temp.splice(i, 1);
    console.log("splice", temp);
    seteducation(temp);
    setTestUpdate(!testUpdate);
  };
  const removemployement = (i) => {
    const temp = employement;
    employement.splice(i, 1);
    console.log("splice", temp);
    setemployement(temp);
    setTestUpdate(!testUpdate);
  };
  const [details, setdetails] = useState({
    degreetitle: "",
    institute: "",
    start: "",
    end: "",
    status: "",
  });

  const handleeducationdetails = async (e) => {
    let name, value;

    name = e.target.name;
    value = e.target.value;

    await setdetails({
      ...details,
      [name]: value,
    });
  };

  const handleempinput = async (e) => {
    let name, value;

    name = e.target.name;
    value = e.target.value;

    await setempdetails({
      ...empdetails,
      [name]: value,
    });
  };

  const [employement, setemployement] = useState([]);
  const [emp, setEmp] = useState({
    profilepic: "",
    firstname: "",
    lastname: "",
    dob: "",
    cnic: "",
    gender: "",
    martialStatus: "",
    religion: "",
    jobtitle: "",
    username: "",
    primaryemail: "",
    secondaryemail: "",
    password: "",
    primaryphone: "",
    secondaryphone: "",
    permanentaddress: "",
    temporaryaddress: "",
    province: "",
    city: "",
    postalCode: "",
    departments: "",
    designation: "",
    joiningdate: "",
    educationdetails: "",
    employementhistory: "",

    currentSalary: "",
    employementstatus: "",
    //bank information
    bankname: "",
    paymentmode: "",
    accounttitle: "",
    accountno: "",
    IBAN: "",
    branchcode: "",
    country: "",
    //degree info
  });
  const addeducation = () => {
    var temp = education;
    temp.push({
      degreetitle: details.degreetitle,
      institute: details.institute,
      start: details.start,
      end: details.end,
      status: details.status,
    });
    seteducation(temp);
    setEmp({ ...emp, educationdetails: education });
  };

  return (
    <>
      <Card style={{ marginTop: 100 }}>
        <Card.Body>
          <Form onSubmit={submitFormData}>
            <h3>Previous Information</h3>
            <hr></hr>

            <Row>
              <Container>
                {" "}
                <Row className="my-3"></Row>
              </Container>
              <div className="my-3 d-flex ">
                <div className="d-flex justify-content-end">
                  <Button
                    className="btn ml-2"
                    // onClick={()=>{props.onClick}}
                    onClick={() => {
                      setShowChildModel(true);
                    }}
                  >
                    Add Employement
                  </Button>
                </div>
              </div>

              <Row className="my-3">
                <Table striped bordered hover className="ml-4">
                  <thead>
                    <tr>
                      <th>#</th>
                      <th style={{ textAlign: "center" }}>Company</th>
                      <th style={{ textAlign: "center" }}>Position</th>
                      <th style={{ textAlign: "center" }}>Start Date</th>
                      <th style={{ textAlign: "center" }}>Left Date</th>
                      <th style={{ textAlign: "center" }}>Duration</th>
                      <th style={{ textAlign: "center" }}>Remove</th>
                    </tr>
                  </thead>
                  <tbody>
                    {employement &&
                      employement.map((d, i) => {
                        return (
                          <>
                            <tr key={i}>
                              <th>{i + 1}</th>
                              <td>{d.company}</td>
                              <td>{d.position}</td>
                              <td>{d.joiningdate}</td>
                              <td>{d.resignationdate}</td>
                              <td>{d.duration}</td>
                              <td>
                                <i
                                  class="fa fa-trash-can"
                                  aria-hidden="true"
                                  style={{ color: "red" }}
                                  onClick={() => removemployement(i)}
                                ></i>
                              </td>
                            </tr>
                          </>
                        );
                      })}
                    {/* {props.employement} */}
                  </tbody>
                </Table>
              </Row>
            </Row>
            {/* </div> */}
            <Modal
              aria-labelledby="contained-modal-title-vcenter"
              centered
              show={childModel}
              onHide={Closechildmodal}
              size="lg"
            >
              <Modal.Header closeButton>
                <Modal.Title
                  id="contained-modal-title-vcenter "
                  style={{ textAlign: "center" }}
                >
                  <h5>Employement Details</h5>
                </Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <Container fluid>
                  <Row>
                    <div className="py-3">
                      <h4>Previous Employment</h4>
                      <hr
                        style={{
                          fontWeight: "bold",
                          borderWidth: "2px",
                          border: "1px solid black",
                        }}
                      ></hr>
                    </div>

                    <Col>
                      <Form.Group
                        as={Col}
                        controlId="formGridempcompany"
                        className="formmargin"
                      >
                        <Form.Label>Company</Form.Label>
                        <Form.Control
                          type="text"
                          required
                          name="company"
                          placeholder="company name.."
                          // value={empdetails.company}
                          defaultValue={values.company}
                          onChange={handleempinput}

                      
                          // disabled={disableFields}
                        />
                      </Form.Group>
                    </Col>
                    <Col>
                      <Form.Group
                        as={Col}
                        controlId="formGridLastName"
                        className="formmargin"
                      >
                        <Form.Label>Position</Form.Label>
                        <Form.Control
                          type="text"
                          required
                          name="position"
                          placeholder="position.."
                          // value={empdetails.position}
                          defaultValue={values.position}
                          onChange={
                            handleempinput
                          }
                          // onChange={handleempinput}
                          // disabled={disableFields}
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                  <Row>
                    <Col>
                      <Form.Group
                        as={Col}
                        controlId="formGridFirstName"
                        className="formmargin"
                      >
                        <Form.Label>Joining Date</Form.Label>
                        <Form.Control
                          type="date"
                          required
                          name="joiningdate"
                          placeholder="joining date"
                          defaultValue={values.joiningdate}
                          // value={empdetails.joiningdate}
                          onChange={async (e) =>
                            await handleempinputJoiningDate(e)
                          }
                          // disabled={disableFields}
                        />
                      </Form.Group>
                    </Col>
                    <Col>
                      <Form.Group
                        as={Col}
                        controlId="formGridFirstName"
                        className="formmargin"
                      >
                        <Form.Label>Resignation Date</Form.Label>
                        <Form.Control
                          type="date"
                          required
                          name="resignationdate"
                          placeholder="resignation date"
                          defaultValue={values.resignationdate}
                          // value={empdetails.resignationdate}
                          onChange={async (e) =>
                            await handleempinputResignationDate(e)
                          }
                          // disabled={disableFields}
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                  <Row>
                    <Form.Label>Job Description</Form.Label>
                    <FormGroup
                      as={Col}
                      controlId="formGridFirstName"
                      className="formmargin"
                    >
                      <Form.Control
                        as="textarea"
                        rows={"3"}
                        style={{ resize: "none" }}
                        required
                        name="jobdescription"
                        placeholder="Tell us about your job role & experience in previous company "
                        //   value={empdetails.jobdescription}
                        defaultValue={values.jobdescription}
                        onChange={() => {
                          handleFormData("jobdescription");
                          handleempinput();
                        }}
                        //   onChange={handleempinput}
                        // disabled={disableFields}
                      />
                    </FormGroup>
                  </Row>
                  <div className="d-flex justify-content-center my-3">
                    <Button
                      className="btn ml-2"
                      onClick={() => {
                        addhistory();
                        Closechildmodal();
                      }}
                    >
                      Add Employement
                    </Button>
                  </div>
                </Container>
              </Modal.Body>
            </Modal>

            <div style={{ display: "flex" }}>
              <Button variant="primary" onClick={prevStep}>
                Previous
              </Button>

              <Button
                variant="primary"
                type="submit"
                style={{ marginLeft: "2%" }}
                onClick={handleSubmit}
              >
                Submit
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </>
  );
};

export default StepFour;
