import React, { useState } from "react";
// import { Form, Card, Button } from "react-bootstrap";
import validator from "validator";
import {
  Modal,
  Form,
  Col,
  Row,
  Container,
  FormGroup,
  Button,
  InputGroup
} from "react-bootstrap";
// import validator from "validator";
import Card from "react-bootstrap/Card";
// creating functional component ans getting props from app.js and destucturing them
const StepSeven = ({ nextStep, handleFormData, prevStep, values ,data}) => {
 
  //creating error state for validation
  const [error, setError] = useState(false);
  const [education, seteducation] = useState([]);
  const [employement, setemployement] = useState([]);
  const Closechildmodal = () => setShowChildModel(false);
  const Closechildmodal1 = () => setShowChildModel1(false);
  const [childModel, setShowChildModel] = useState(false);
  const [childModel1, setShowChildModel1] = useState(false);
  const [empdetails, setempdetails] = useState({
    company: "",
    position: "",
    joiningdate: "",
    resignationdate: "",
    duration: "",
    jobdescription: "",
  });

  const removeitem = (i) => {
    const temp = education;
    temp.splice(i, 1);
    console.log("splice", temp);
    seteducation(temp);
    setTestUpdate(!testUpdate);
  };
console.log("values",values)
  // after form submit validating the form data using validator
  const submitFormData = (e) => {
    e.preventDefault();

    // checking if value of first name and last name is empty show error else take to next step
    if (
      validator.isEmpty(values.institude) ||
      validator.isEmpty(values.desgree) ||
      validator.isEmpty(values.startDate) ||
      validator.isEmpty(values.endDate) ||
      validator.isEmpty(values.status)
    ) {
      setError(true);
    } else {
      nextStep();
    }
  };
  return (
    <>
      <Card style={{ marginTop: 100 }}>
        <Card.Body>
          <Form onSubmit={submitFormData}>
            <h3 style={{color:'rgb(0,105,92)'}}>Education Information</h3>
            <hr></hr>

            <Row>
              <Container>
                {" "}
                <Row className="my-3"></Row>
              </Container>
              <div className="my-3 d-flex ">
                <div className="d-flex justify-content-end">
                  <Button
                    className="btn ml-2"
                    // onClick={()=>{props.onClick}}
                    onClick={() => {
                      setShowChildModel(true);
                    }}
                  >
                    Add Education
                  </Button>
                </div>
              </div>
{data.educationdetails ? (
              <Row className="my-3">
                <Table striped bordered hover className="ml-4">
                  <thead>
                    <tr>
                      <th>#</th>
                      <th style={{ textAlign: "center" }}>Institute</th>
                      <th style={{ textAlign: "center" }}>Degree</th>
                      <th style={{ textAlign: "center" }}>Start Date</th>
                      <th style={{ textAlign: "center" }}>End Date</th>
                      <th style={{ textAlign: "center" }}>Status</th>
                      <th style={{ textAlign: "center" }}>Delete</th>
                    </tr>
                  </thead>
                  <tbody>
                    {education &&
                      education.map((d, i) => {
                        return (
                          <>
                            <tr key={i}>
                              <th>{i + 1}</th>
                              <td>{d.institude}</td>
                              <td>{d.degreetitle}</td>
                              <td>{d.start}</td>
                              <td>{d.end}</td>
                              <td>{d.status}</td>
                              <td>
                                <i
                                  class="fa fa-trash-can"
                                  aria-hidden="true"
                                  style={{ color: "red" }}
                                  onClick={() => removeitem(i)}
                                ></i>
                              </td>
                            </tr>
                          </>
                        );
                      })}
                    
                  </tbody>
                </Table>
              </Row>
              ):("No Data Available")}
            </Row>  

            {/* </div> */}
            <Modal
              aria-labelledby="contained-modal-title-vcenter"
              centered
              show={childModel}
              onHide={Closechildmodal}
              size="lg"
            >
              <Modal.Header closeButton>
                <Modal.Title
                  id="contained-modal-title-vcenter "
                  style={{ textAlign: "center" }}
                >
                  <h5>Education Details</h5>
                </Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <Container fluid>
                  <Row>
                    <div className="py-3">
                      <h4>Education Details</h4>
                      <hr
                        style={{
                          fontWeight: "bold",
                          borderWidth: "2px",
                          border: "1px solid black",
                        }}
                      ></hr>
                    </div>

                    <Col>
                      <Form.Group
                        as={Col}
                        controlId="formGridempcompany"
                        className="formmargin"
                      >
                        <Form.Label>Institude</Form.Label>
                        <Form.Control
                          type="text"
                          required
                          name="company"
                          placeholder="company name.."
                          // defaultValue={empdetails.company}
                          defaultValue={values.institude}
                          onChange={handleFormData("institude")}
                          // onChange={handleempinput}
                          // onChange={handleempinput}
                          // disabled={disableFields}
                        />
                      </Form.Group>
                    </Col>
                    <Col>
                      <Form.Group
                        as={Col}
                        controlId="formGridLastName"
                        className="formmargin"
                      >
                        <Form.Label>Degree</Form.Label>
                        <Form.Control
                          type="text"
                          required
                          name="position"
                          placeholder="position.."
                          // defaultValue={empdetails.position}
                          defaultValue={values.degree}
                          onChange={handleFormData("degree")}
                          // onChange={handleempinput}
                          // disabled={disableFields}
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                 
                  <Row>
                    <Col>
                      <Form.Group
                        as={Col}
                        controlId="formGridFirstName"
                        className="formmargin"
                      >
                        <Form.Label>Start Date</Form.Label>
                        <Form.Control
                          type="date"
                          required
                          name="joiningdate"
                          placeholder="joining date"
                          defaultValue={values.startDate}
                          // defaultValue={empdetails.joiningdate}
                          onChange={async (e) =>
                            await handleempinputJoiningDate(e)
                          }
                          // disabled={disableFields}
                        />
                      </Form.Group>
                    </Col>
                    <Col>
                      <Form.Group
                        as={Col}
                        controlId="formGridFirstName"
                        className="formmargin"
                      >
                        <Form.Label>End Date</Form.Label>
                        <Form.Control
                          type="date"
                          required
                          name="resignationdate"
                          placeholder="resignation date"
                          defaultValue={values.endDate}
                          // defaultValue={empdetails.resignationdate}
                          onChange={async (e) =>
                            await handleempinputResignationDate(e)
                          }
                          // disabled={disableFields}
                        />
                      </Form.Group>
                    </Col>
                    <Col>
                <Form.Group
                  as={Col}
                  controlId="formGridLastName"
                  className="formmargin"
                >
                  <Form.Label>Status</Form.Label>
                  <Form.Select
                    required
                    name="status"
                    placeholder="status"
                    value={values.status}
                    onChange={handleeducationdetails}
                    //   disabled={disableFields}
                  >
                    <option value="" selected hidden disabled>
                      Please Select
                    </option>
                    <option>Completed</option>
                    <option>In progress</option>
                  </Form.Select>
                </Form.Group>
              </Col>
                  </Row>
                 
                  <div className="d-flex justify-content-center my-3">
                    <Button
                      className="btn ml-2"
                      onClick={() => {
                        addhistory();
                        Closechildmodal();
                        values.employementhistory=employement

                      }}
                    >
                      Add Education
                    </Button>
                  </div>
                </Container>
              </Modal.Body>
            </Modal>

            <div style={{ display: "flex" }}>
              <Button variant="primary" onClick={prevStep}>
                Previous 
              </Button>

              <Button
                variant="primary"
                type="submit"
                style={{ marginLeft: "2%" }}
                // onClick={handleSubmit}
                onClick={handleSubmit}
                
              >
                Next
              </Button>

            </div>
          </Form>
        </Card.Body>
      </Card>
      <NotificationContainer/>
    </>
  )
};

export default StepSeven;
