                              controlId="formGridLastName"
                              className="formmargin"
                            >
                              <Form.Label>Account Title</Form.Label>
                              <Form.Control
                                type="text"
                                required
                                name="accounttitle"
                                placeholder="account title.."
                                // value={emp.accounttitle}
                                // value={props.value27}
                                // onChange={handleinput}
                                // onChange={props.onChange}
                                //   disabled={disableFields}
                              />
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group
                              as={Col}
                              controlId="formGridLastName"
                              className="formmargin"
                            >
                              <Form.Label>Account No</Form.Label>
                              <Form.Control
                                type="text"
                                required
                                name="accountno"
                                placeholder="account no.."
                                // value={emp.accountno}
                                // value={props.value28}
                                // onChange={handleinput}
                                // onChange={props.onChange}
                                //   disabled={disableFields}
                              />
                            </Form.Group>
                          </Col>
                        </Row>
                        <Row className="p-0">
                          <Col xxl={4}>
                            <Form.Group
                              as={Col}
                              controlId="formGridLastName"
                              className="formmargin"
                            >
                              <Form.Label> IBAN</Form.Label>
                              <Form.Control
                                type="text"
                                required
                                name="IBAN"
                                placeholder="iban.."
                                // value={emp.IBAN}
                                // value={props.value29}
                                // onChange={handleinput}
                                // onChange={props.onChange}
                                //  disabled={disableFields}
                              />
                            </Form.Group>
                          </Col>
                         
                          <Col xxl={4}>
                            <Form.Group
                              as={Col}
                              controlId="formGridLastName"
                              className="formmargin"
                            >
                              <Form.Label>Branch code</Form.Label>
                              <Form.Control
                                type="text"
                                required
                                name="branchcode"
                                placeholder="branch code.."
                                // value={emp.branchcode}
                                // value={props.value30}
                                // onChange={handleinput}
                                // onChange={props.onChange}
                                //  disabled={disableFields}
                              />
                            </Form.Group>
                          </Col>
                        </Row>
                  </div>
                )
              }
    default:
      return "unknown step";
  }
}

const EmployeeData = () => {
  const classes = useStyles();
  const [activeStep, setActiveStep] = useState(0);
  const [skippedSteps, setSkippedSteps] = useState([]);
  const steps = getSteps();

  const isStepOptional = (step) => {
    return step === 1 || step === 2;
  };

  const isStepSkipped = (step) => {
    return skippedSteps.includes(step);
  };

  const handleNext = () => {
    setActiveStep(activeStep + 1);
    setSkippedSteps(skippedSteps.filter((skipItem) => skipItem !== activeStep));
  };

  const handleBack = () => {
    setActiveStep(activeStep - 1);
  };

  const handleSkip = () => {
    if (!isStepSkipped(activeStep)) {
      setSkippedSteps([...skippedSteps, activeStep]);
    }
    setActiveStep(activeStep + 1);
  };

  return (
    <div className="content-wrapper ">
   
       <div className="col">
                     <h3 className="page-title">Add Employees</h3>
                     <ul
                      className="breadcrumb"
                      style={{ backgroundColor: "#f7f7f7" }}
                    >
                      <li className="breadcrumb-item">
                        <Link to="/" style={{ color: "#1f1f1f" }}>
                          Dashboard
                        </Link>
                      </li>
                      <li className="breadcrumb-item active">Add Employee</li>
                    </ul>
                    
                  </div>
                  <div className=" d-flex justify-content-center " style={{marginTop:'5%'}}>
        <Card style={{height:"40%"}}>
          <div style={{height:'40%'}}>
          <Card.Body >
        <div style={{display:'flex',justifyContent:'center'}}>
      <Stepper alternativeLabel activeStep={activeStep}>
        {steps.map((step, index) => {
          const labelProps = {};
          const stepProps = {};
          if (isStepOptional(index)) {
            labelProps.optional = (
              <Typography
                variant="caption"
                align="center"
                style={{ display: "block" }}
              >
                optional
              </Typography>
            );
          }
          if (isStepSkipped(index)) {
            stepProps.completed = false;
          }
          return (
            <Step {...stepProps} key={index}>
              <StepLabel {...labelProps}>{step}</StepLabel>
            </Step>
          );
        })}
      </Stepper>

      {activeStep === steps.length ? (
        <Typography variant="h3" align="center">
          Thank You
        </Typography>
      ) : (
        <>
          <form>{getStepContent(activeStep)}</form>
          <Button
            className={classes.button}
            disabled={activeStep === 0}
            onClick={handleBack}
          >
            back
          </Button>
          {isStepOptional(activeStep) && (
            <Button
              className={classes.button}
              variant="contained"
              color="primary"
              onClick={handleSkip}
            >
              skip
            </Button>
          )}
          <Button
            className={classes.button}
            variant="contained"
            color="primary"
            onClick={handleNext}
          >
            {activeStep === steps.length - 1 ? "Finish" : "Next"}
          </Button>
        </>
      )}
    </div>
    </Card.Body>
    </div>
     </Card>
    </div>
     </div>
     
  );
};

export default EmployeeData;
