import React, { useState } from "react";
// import { Form, Card, Button } from "react-bootstrap";
import validator from "validator";
import {
  Modal,
  Form,
  Col,
  Row,
  Container,
  FormGroup,
  Button,
} from "react-bootstrap";
// import validator from "validator";
import Card from "react-bootstrap/Card";
// creating functional component ans getting props from app.js and destucturing them
const StepSix = ({ nextStep, handleFormData, prevStep, values }) => {
  const [emp, setEmp] = useState({
    profilepic: "",
    firstname: "",
    lastname: "",
    dob: "",
    cnic: "",
    gender: "",
    martialStatus: "",
    religion: "",
    jobtitle: "",
    username: "",
    primaryemail: "",
    secondaryemail: "",
    password: "",
    primaryphone: "",
    secondaryphone: "",
    permanentaddress: "",
    temporaryaddress: "",
    province: "",
    city: "",
    postalCode: "",
    departments: "",
    designation: "",
    joiningdate: "",
    educationdetails: "",
    employementhistory: "",

    currentSalary: "",
    employementstatus: "",
    //bank information
    bankname: "",
    paymentmode: "",
    accounttitle: "",
    accountno: "",
    IBAN: "",
    branchcode: "",
    country: "",
    //degree info
  });
  
  //creating error state for validation
  const [error, setError] = useState(false);

  // after form submit validating the form data using validator
  const submitFormData = (e) => {
    e.preventDefault();

    // checking if value of first name and last name is empty show error else take to next step
    if (
      validator.isEmpty(values.bankname) ||
      validator.isEmpty(values.accounttitile) ||
      validator.isEmpty(values.accountno) ||
      validator.isEmpty(values.iban) ||
      validator.isEmpty(values.branchcode)
    ) {
      setError(true);
    } else {
      nextStep();
    }
  };
  return (
    <>
      <Card style={{ marginTop: 100 }}>
        <Card.Body>
          <Form onSubmit={submitFormData}>
            <h3>Bank Details</h3>
            <hr></hr>
            <Row>
              <Col>
                <Form.Group
                  as={Col}
                  controlId="formGridLastName"
                  className="formmargin"
                >
                  <Form.Label>Bank Name</Form.Label>
                  <Form.Select
                    required
                    name="bankname"
                    placeholder="bank name.."
                    // defaultValue={emp.bankname}
                    defaultValue={values.bankname}
                    // onChange={handleinput}
                    onChange={handleFormData("bankname")}
                    //   disabled={disableFields}
                  >
                    <option>Please Select</option>
                    <option>MCB Limited</option>
                    <option>Bank Islami Limited</option>
                    <option>Allied Bank Limited</option>
                    <option>Bank Al-Habib Limited</option>
                    <option>Faysal Bank Limited</option>
                    <option>Mezaan Bank Limited</option>
                    <option>National Bank of Pakistan</option>
                    <option>MCB Islamic Limited</option>
                    <option>HBL</option>
                    <option>UBL</option>
                    <option>Askari Bank </option>
                  </Form.Select>
                </Form.Group>
              </Col>
              <Col>
                <Form.Group
                  as={Col}
                  controlId="formGridLastName"
                  className="formmargin"
                >
                  <Form.Label>Account Title</Form.Label>
                  <Form.Control
                    type="text"
                    required
                    name="accounttitle"
                    placeholder="account title.."
                    // defaultValue={emp.accounttitle}
                    defaultValue={values.accounttitle}
                    // value={props.value27}
                    // onChange={handleinput}
                    onChange={handleFormData("accounttitile")}
                    // onChange={props.onChange}
                    //   disabled={disableFields}
                  />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group
                  as={Col}
                  controlId="formGridLastName"
                  className="formmargin"
                >
                  <Form.Label>Account No</Form.Label>
                  <Form.Control
                    type="text"
                    required
                    name="accountno"
                    placeholder="account no.."
                    // defaultValue={emp.accountno}
                    defaultValue={values.accountno}
                    // value={props.value28}
                    // onChange={handleinput}
                    onChange={handleFormData("accountno")}
                    // onChange={props.onChange}
                    //   disabled={disableFields}
                  />
                </Form.Group>
              </Col>
            </Row>
            <Row className="p-0">
              <Col xxl={4}>
                <Form.Group
                  as={Col}
                  controlId="formGridLastName"
                  className="formmargin"
                >
                  <Form.Label> IBAN</Form.Label>
                  <Form.Control
                    type="text"
                    required
                    name="IBAN"
                    placeholder="iban.."
                    // defaultValue={emp.IBAN}
                    defaultValue={values.iban}
                    // value={props.value29}
                    // onChange={handleinput}
                    onChange={handleFormData("iban")}
                    // onChange={props.onChange}
                    //  disabled={disableFields}
                  />
                </Form.Group>
              </Col>

              <Col xxl={4}>
                <Form.Group
                  as={Col}
                  controlId="formGridLastName"
                  className="formmargin"
                >
                  <Form.Label>Branch code</Form.Label>
                  <Form.Control
                    type="text"
                    required
                    name="branchcode"
                    placeholder="branch code.."
                    // defaultValue={emp.branchcode}
                    defaultValue={values.branchcode}
                    // value={props.value30}
                    // onChange={handleinput}
                    onChange={handleFormData("branchcode")}
                    // onChange={props.onChange}
                    //  disabled={disableFields}
                  />
                </Form.Group>
              </Col>
            </Row>

            <div style={{ display: "flex" }}>
              <Button variant="primary" onClick={prevStep}>
                Previous
              </Button>

              <Button
                variant="primary"
                type="submit"
                style={{ marginLeft: "2%" }}
              >
                Next
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </>
  );
};

export default StepSix;
