import React, { useState } from "react";
// import { Form, Card, Button } from "react-bootstrap";
import {
  Modal,
  Form,
  Col,
  Row,
  Container,
  FormGroup,
  Button,
} from "react-bootstrap";
import validator from "validator";
import Card from "react-bootstrap/Card";
import { Link } from "react-router-dom";
// creating functional component ans getting props from app.js and destucturing them
const StepOne = ({ nextStep, handleFormData, values }) => {

  const [emp, setEmp] = useState({
        
        firstname: "",
        lastname: "",
        dob: "",
        cnic: "",
        gender: "",
        martialStatus: "",
        religion: "",
      });
      const [employement, setemployement] = useState([]);
      const [empdetails, setempdetails] = useState({
            company: "",
            position: "",
            joiningdate: "",
            resignationdate: "",
            duration: "",
            jobdescription: "",
          });
  //creating error state for validation
  const [error, setError] = useState(false);
  const [education, seteducation] = useState([]);
  // after form submit validating the form data using validator
  const submitFormData = (e) => {
    e.preventDefault();

    // checking if value of first name and last name is empty show error else take to step 2
    console.log("value", values);
    if (
      validator.isEmpty(values.firstName) ||
      validator.isEmpty(values.lastName) ||
      validator.isEmpty(values.gender) ||
      validator.isEmpty(values.cnic) ||
      validator.isEmpty(values.dob) ||
      validator.isEmpty(values.martialStatus) ||
      validator.isEmpty(values.religion)
    ) {
      setError(true);
      console.log("setError");
    } else {
      nextStep();
      console.log("nextstep");
    }
  };
     const addhistory = () => {
        var empl = employement;
        empl.push({
          company: empdetails.company,
          position: empdetails.position,
          joiningdate: empdetails.joiningdate,
          resignationdate: empdetails.resignationdate,
          duration: empdetails.duration,
          jobdescription: empdetails.jobdescription,
        });
        console.log("emplllllllll", empl);
        setemployement(empl);
        setEmp({ ...emp, employementhistory: empl });
      };
    
      let name, value;
      const handleinput = (e) => {
        console.log(e);
        name = e.target.name;
        value = e.target.value;
        setEmp({ ...emp, [name]: value });
      };
      const addeducation = () => {
    var temp = education;
    temp.push({
      degreetitle: details.degreetitle,
      institute: details.institute,
      start: details.start,
      end: details.end,
      status: details.status,
    });
    seteducation(temp);
    setEmp({ ...emp, educationdetails: education });
  };


  return (
    <>
      {/* <div className="col">
    <h3 className="page-title">Add Employees</h3>
  <ul className="breadcrumb" style={{ backgroundColor: "#f7f7f7" }}>
     <li className="breadcrumb-item">
       <Link to="/" style={{ color: "#1f1f1f" }}>
         Dashboard
       </Link>
     </li>
     <li className="breadcrumb-item active">Add Employee</li>
  </ul>
  </div> */}
      <div>
        <Card style={{ marginTop: 100 }}>
          <Card.Body>
            <Form onSubmit={submitFormData}>
              <h4>Personal Information</h4>
              <hr></hr>
              <Row>
                <Col xxl="6" xl="6" lg="6" md="6">
                  <Form.Group className="mb-3">
                    <Form.Label>First Name</Form.Label>
                    <Form.Control
                      required
                      // style={{ border: error ? "2px solid red" : "" }}
                      name="firstName"
                      // defaultValue={values.firstName}
                      defaultValue={emp.firstname}
                      type="text"
                      placeholder="First Name"
                      // onChange={handleFormData("firstName")}
                      onChange={handleinput}
                    />
                  </Form.Group>
                </Col>
                <Col xxl="6" xl="6" lg="6" md="6">
                  <Form.Group className="mb-3">
                    <Form.Label>Last Name</Form.Label>
                    <Form.Control
                      required
                      // style={{ border: error ? "2px solid red" : "" }}
                      name="lastName"
                      defaultValue={emp.lastname}
                      // defaultValue={values.lastName}
                      type="text"
                      placeholder="Last Name"
                      // onChange={handleFormData("lastName")}
                      onChange={handleinput}
                    />
                  </Form.Group>
                </Col>
              </Row>
              <Row>
                <Col xxl="6" xl="6" lg="6" md="6">
                  <Form.Group
                    as={Col}
                    controlId="formGridgender"
                    className="formmargin"
                  >
                    <Form.Label>Gender</Form.Label>
                    <Form.Select
                      required
                      name="gender"
                      // defaultValue={values.gender}
                      defaultValue={emp.gender}
                      // value={props.value2}
                      //   onChange={handleinput}
                      // onChange={props.onChange}
                      // onChange={handleFormData("gender")}
                      onChange={handleinput}
                    >
                      <option value="" selected hidden disabled>
                        Please Select
                      </option>
                      <option>Male</option>
                      <option>Female</option>
                      <option>Other</option>
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col xl="6" lg="6" md="6">
                  <Form.Group
                    as={Col}
                    controlId="formGridFirstName"
                    className="formmargin"
                  >
                    <Form.Label>National ID </Form.Label>
                    <Form.Control
                      type="number"
                      required
                      name="cnic"
                      placeholder="CNIC"
                      defaultValue={emp.cnic}
                      // defaultValue={values.cnic}
                      // value={props.value3}
                      //   onChange={handleinput}
                      // onChange={props.onChange}
                      //   disabled={disableFields}
                      // onChange={handleFormData("cnic")}
                      onChange={handleinput}
                    />
                  </Form.Group>
                </Col>
              </Row>
              <Row>
                <Col xxl="6" xl="6" lg="6">
                  <Form.Group
                    as={Col}
                    controlId="formGridLastName"
                    className="formmargin"s
                  >
                    <Form.Label>D-0-B</Form.Label>
                    {/* <DatePicker
                          
                            required
                            selected={emp.dob}
                            onChange={(date)=>{setEmp({...emp,dob:moment(date).format('dd/mm/yyyy')})}}
                            // dateFormat='d MMMM, yyyy'
                            dateFormat='dd/mm/yyyy'
                            dropDownMode="select"
                          /> */}
                    <Form.Control
                      type="date"
                      required
                      placeholder="dd/mm/yyyy"
                      name="dob"
                      defaultValue={emp.dob}
                      // value={props.value4}
                      // defaultValue={values.dob}
                      //   onChange={handleinput}
                      // onChange={props.onChange}
                      //   disabled={disableFields}
                      // onChange={handleFormData("dob")}
                      onChange={handleinput}
                    />
                  </Form.Group>
                </Col>

                <Col xl="6" lg="6">
                  <Form.Group
                    as={Col}
                    controlId="formGridMartialStatus"
                    className="formmargin"
                  >
                    <Form.Label>Martial Status</Form.Label>
                    <Form.Select
                      required
                      name="martialStatus"
                      placeholder="martial status.."
                      defaultValue={emp.martialStatus}
                      // defaultValue={values.martialStatus}
                      // value={props.value5}
                      //   onChange={handleinput}
                      // onChange={props.onChange}
                      //   disabled={disableFields}
                      // onChange={handleFormData("martialStatus")}
                      onChange={handleinput}
                    >
                      <option value="" selected hidden disabled>
                        Please Select
                      </option>
                      <option>Single</option>
                      <option>Married</option>
                      <option>Divorced</option>
                      <option>widow</option>
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col xl="6" lg="6">
                  <Form.Group
                    as={Col}
                    controlId="formGridLastName"
                    className="formmargin"
                  >
                    <Form.Label>Religion</Form.Label>
                    <Form.Select
                      required
                      name="religion"
                      placeholder="Religion"
                      defaultValue={emp.religion}
                      // defaultValue={values.religion}
                      // value={props.value6}
                      //   onChange={handleinput}
                      // onChange={props.onChange}
                      //   disabled={disableFields}
                      // onChange={handleFormData("religion")}
                      onChange={handleinput}
                    >
                      <option value="" selected hidden disabled>
                        Please Select
                      </option>
                      <option>Islam</option>
                      <option>Christianity</option>
                      <option>Buddhism</option>
                      <option> Sikhism</option>
                      <option> Hinduism</option>
                      <option>Bahá’í</option>
                      <option>Confucianism</option>
                      <option>Jainism</option>
                      <option>Judaism</option>
                      <option>Zoroastrianism</option>
                      <option>Druze</option>
                    </Form.Select>
                  </Form.Group>
                </Col>
              </Row>
              <Button variant="primary" type="submit">
                Next
              </Button>
            </Form>
          </Card.Body>
        </Card>
      </div>
    </>
  );
};

export default StepOne;
