import React from "react";
import { useState } from "react";
import {
  Typography,
  TextField,
  Button,
  Stepper,
  Step,
  StepLabel,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import {
  Modal,

  Form,
  Col,
  Row,
  Container,
  FormGroup,
} from "react-bootstrap";
import Card from 'react-bootstrap/Card';
import Table from "react-bootstrap/Table";
import { Link } from 'react-router-dom'

// const [emp, setEmp] = useState({
//   profilepic: "",
//   firstname: "",
//   lastname: "",
//   dob: "",
//   cnic: "",
//   gender: "",
//   martialStatus: "",
//   religion: "",
//   jobtitle: "",
//   username: "",
//   primaryemail: "",
//   secondaryemail: "",
//   password: "",
//   primaryphone: "",
//   secondaryphone: "",
//   permanentaddress: "",
//   temporaryaddress: "",
//   province: "",
//   city: "",
//   postalCode: "",
//   departments: "",
//   designation: "",
//   joiningdate: "",
//   educationdetails: "",
//   employementhistory: "",

//   currentSalary: "",
//   employementstatus: "",
//   //bank information
//   bankname: "",
//   paymentmode: "",
//   accounttitle: "",
//   accountno: "",
//   IBAN: "",
//   branchcode: "",
//   country:"",
  //degree info
// });


// const [testUpdate, setTestUpdate] = useState(false);
// const [employement, setemployement] = useState([]);

// const removemployement = (i) =>{
//   const temp = employement;
//   employement.splice(i,1); 
//   console.log("splice",temp)
  // setemployement(temp);
  // setTestUpdate(!testUpdate)
// }


// const [empdetails, setempdetails] = useState({
//   company: "",
//   position: "",
//   joiningdate: "",
//   resignationdate: "",
//   duration: "",
//   jobdescription: "",
// });

// const addhistory = () => {
//   var empl = employement;
//   empl.push({
//     company: empdetails.company,
//     position: empdetails.position,
//     joiningdate: empdetails.joiningdate,
//     resignationdate: empdetails.resignationdate,
//     duration: empdetails.duration,
//     jobdescription: empdetails.jobdescription,
//   });
//   console.log("emplllllllll", empl);
//   setemployement(empl);
//   setEmp({ ...emp, employementhistory: empl });
// };

const useStyles = makeStyles((theme) => ({
  button: {
    marginRight: theme.spacing(1),
  },
}));

function getSteps() {
  return [
    "Personal Information",
    "Address & Region",
    "Education Information",
    "Previous Information",
    "Employement Details",
    "Bank Details"
  ];
}

function getStepContent({props,activeStep}) {
  // console
  const step =activeStep
  const [childModel1, setShowChildModel1] = useState(false);
  const [childModel, setShowChildModel] = useState(false);
  const Closechildmodal = () => setShowChildModel(false);
  const Closechildmodal1 = () => setShowChildModel1(false);


 

  switch ({prop,step}) {
    case 0:
      return (
        <>
        <div style={{height:'20%'}}>
          
          <div className="w-75" id="responsivewidth" style={{width:'60%', height:'40%'}}>
                  <Col>
                    <Row >
                      <Col xxl="6" xl="6" lg="6" md="6">
                        <Form.Group
                          as={Col}
                          controlId="formGridFirstName"
                          className="formmargin"
                        >
                          <Form.Label>First Name</Form.Label>
                          <Form.Control
                            type="text"
                            required
                            name="firstname"
                            placeholder="First Name"
                            // value={emp.firstname}
                            value={props.value}
                            // onChange={handleinput}
                            onChange={props.onChange}
                          />
                        </Form.Group>
                      </Col>
                      <Col xxl="6" xl="6" lg="6" md="6">
                        <Form.Group
                          as={Col}
                          controlId="formGridLastName"
                          className="formmargin"
                        >
                          <Form.Label>Last Name</Form.Label>
                          <Form.Control
                            type="text"
                            required
                            name="lastname"
                            placeholder="Last Name"
                            // value={emp.lastname}
                            value={props.value1}
                            // onChange={handleinput}
                            onChange={props.onChange}
                          />
                        </Form.Group>
                      </Col>
                    </Row>
                    <Row>
                      <Col xxl='6' xl='6' lg='6' md='6'>
                        <Form.Group
                          as={Col}
                          controlId="formGridgender"
                          className="formmargin"
                        >
                          <Form.Label>Gender</Form.Label>
                          <Form.Select
                            required
                            name="gender"
                            // value={emp.gender}
                            value={props.value2}
                            // onChange={handleinput}
                            onChange={props.onChange}
                          >
                            <option value="" selected hidden disabled>
                              Please Select
                            </option>
                            <option>Male</option>
                            <option>Female</option>
                            <option>Other</option>
                          </Form.Select>
                        </Form.Group>
                      </Col>
                      <Col xl='6' lg="6" md="6">
                        <Form.Group
                          as={Col}
                          controlId="formGridFirstName"
                          className="formmargin"
                        >
                          <Form.Label>National ID </Form.Label>
                          <Form.Control
                            type="number"
                            required
                            name="cnic"
                            placeholder="CNIC"
                            // value={emp.cnic}
                            value={props.value3}
                            // onChange={handleinput}
                            onChange={props.onChange}
                            //   disabled={disableFields}
                          />
                        </Form.Group>
                      </Col>
                    </Row>
                    <Row>
                      <Col xxl='6' xl='6' lg='6'>
                        <Form.Group
                          as={Col}
                          controlId="formGridLastName"
                          className="formmargin"
                        >
                          <Form.Label>D-0-B</Form.Label>
                          {/* <DatePicker
                            required
                            selected={emp.dob}
                            onChange={(date)=>{setEmp({...emp,dob:moment(date).format('dd/mm/yyyy')})}}
                            // dateFormat='d MMMM, yyyy'
                            dateFormat='dd/mm/yyyy'
                            dropDownMode="select"
                          /> */}
                          <Form.Control
                            type="date"
                            required
                            placeholder="dd/mm/yyyy"
